      // const [loading, setLoading] = useState([]);

      useEffect(() => {
        const getAllData = () => {

          console.log("Loading...");

          const request = new Request();
          request.get('/api/pins')
          .then((data) => {
              setEventData(data);
          })
      }
        getAllData();
        console.log(eventData);
      }, []);



      if (loadError) return "Error loading maps"
      if (!isLoaded) return "Loading..."

      return (
        <GoogleMap 

        mapContainerStyle={mapContainerStyle} 
        zoom={8} 
        center={center}
        options={options}
